/**
 * Add security metadata on the decorated method.
 *
 * ## Examples
 * ### On method
 *
 * ```typescript
 * @Controller("/")
 * class ModelCtrl {
 *    @Security("write:calendars")
 *    async method() {}
 * }
 * ```
 *
 * @param name
 * @param scopes
 * @decorator
 * @swagger
 * @schema
 * @classDecorator
 * @operation
 */
export declare function Security(name: string, ...scopes: string[]): Function;
/**
 * Add security metadata on the decorated method.
 *
 * You can use it to add multiple authentication types.
 * See <https://swagger.io/docs/specification/authentication/#multiple>.
 *
 * ## Examples
 * ### On method
 *
 * ```typescript
 * @Controller("/")
 * class ModelCtrl {
 *    @Security([{ "A": ["scope-1"] }, { "B": [], "C": ["scope-2", "scope-3"]}])
 *    async method() {}
 * }
 * ```
 * this will add the following security metadata
 * ```yaml
 * security: # A OR (B AND C)
 *   - A: ["scope-1"]
 *   - B: []
 *     C: ["scope-2", "scope-3"]
 * ```
 *
 * @param security
 * @decorator
 * @swagger
 * @schema
 * @classDecorator
 * @operation
 */
export declare function Security(security: OpenSpecSecurity): Function;
