/**
 * Inject the error in the parameters
 *
 * ### Example
 *
 * ```typescript
 * @SocketMiddlewareError()
 * export class MyMiddleware {
 *
 *   myMethod(@SocketErr err: any, @Args() arguments: any[]) {
 *
 *   }
 * }
 * ```
 *
 * @decorator
 * @param target
 * @param propertyKey
 * @param index
 */
export declare function SocketErr(target: any, propertyKey: string, index: number): any;
